function bgChangeGreen(e){e.style.backgroundColor="green",e.setAttribute("color","green")}function bgChangeRed(e){e.style.backgroundColor="red",e.setAttribute("color","red")}function bgChangeBlue(e){e.style.backgroundColor="blue",e.setAttribute("color","blue")}function bgChangeYellow(e){e.style.backgroundColor="yellow",e.setAttribute("color","yellow")}function checkColors(){const e=document.querySelector(".red"),t=document.querySelector(".green"),o=document.querySelector(".blue"),r=document.querySelector(".yellow");if("red"===e.getAttribute("color")&&"green"===t.getAttribute("color")&&"blue"===o.getAttribute("color")&&"yellow"===r.getAttribute("color")){const l=document.querySelector(".link");l.style.display="inherit"}}document.addEventListener("click",function(t){if(t.target.classList.contains("color")){let e=t.target;null===e.getAttribute("color")||"yellow"===e.getAttribute("color")?bgChangeGreen(e):"green"===e.getAttribute("color")?bgChangeRed(e):"red"===e.getAttribute("color")?bgChangeBlue(e):"blue"===e.getAttribute("color")&&bgChangeYellow(e)}checkColors()});